import React, { useRef, useState } from 'react';

const imageData = [
  {
    id: 1,
    url: 'https://picsum.photos/200/300?random=1',
    title: 'Image 1',
    description: 'This is image 1',
  },
  {
    id: 2,
    url: 'https://picsum.photos/200/300?random=2',
    title: 'Image 2',
    description: 'This is image 2',
  },
  // Add more image objects here
  // ...
];

function SwipeComponent() {
  const containerRef = useRef(null);
  const [startX, setStartX] = useState(0);
  const [endX, setEndX] = useState(0);
  const [deltaX, setDeltaX] = useState(0);
  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  function handleTouchStart(e) {
    setStartX(e.touches[0].clientX);
  }

  function handleTouchMove(e) {
    setEndX(e.touches[0].clientX);
  }

  function handleTouchEnd() {
    setDeltaX(endX - startX);

    if (deltaX > 0) {
      // Swiped right, move to the previous image
      setCurrentImageIndex((prevIndex) => prevIndex === 0 ? imageData.length - 1 : prevIndex - 1);
    } else if (deltaX < 0) {
      // Swiped left, move to the next image
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % imageData.length);
    }
  }

  const currentImage = imageData[currentImageIndex];

  return (
    <div
      ref={containerRef}
      onTouchStart={handleTouchStart}
      onTouchMove={handleTouchMove}
      onTouchEnd={handleTouchEnd}
      className="w-full h-screen bg-gray-200 flex justify-center items-center"
    >
      <img
        src={currentImage.url}
        alt={currentImage.title}
        className="max-w-full max-h-full"
      />
    </div>
  );
}

export default SwipeComponent;
