import React, { useState } from 'react';
import { Swiper, SwiperSlide } from "swiper/react";
import gorilla from '../img/gorilla.png';
import eagle from '../img/eagle.png';
import panda from '../img/panda.png';
import crocodile from '../img/crocodile.png';
import bluePlay from '../img/bluePlay.png';
import donate from '../img/donate.png';






// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";


import SwiperCore, { Pagination, Navigation } from 'swiper';

SwiperCore.use([Pagination, Navigation]);

const data = [
  { id: 1, source: eagle, decs: "Eagle's name is Aria. She is 5 years old. Aria symbolizes freedom, strength, and grace, soaring through the skies with majestic beauty"},
  { id: 2, source: panda, decs: 'Panda’s name is Bei Bei. He is 2 years old. Bei Bei is from China. He loves bamboos.'},
  { id: 3, source: gorilla, decs:"Gorilla's name is Koko. He is 10 years old. Koko is a powerful and intelligent creature from the lush forests. "},
  { id: 4, source: crocodile, decs:"Crocodile's name is Rex. He is 15 years old. Rex is a formidable predator, lurking in the waters with stealth and power. "},
  { id: 5, source: eagle, decs: "Eagle's name is Aria. She is 5 years old. Aria symbolizes freedom, strength, and grace, soaring through the skies with majestic beauty"},
  { id: 6, source: panda, decs: 'Panda’s name is Bei Bei. He is 2 years old. Bei Bei is from China. He loves bamboos.'},
  { id: 7, source: gorilla, decs:"Gorilla's name is Koko. He is 10 years old. Koko is a powerful and intelligent creature from the lush forests. "},
  { id: 8, source: crocodile, decs:"Crocodile's name is Rex. He is 15 years old. Rex is a formidable predator, lurking in the waters with stealth and power. "},
];
function Swip() {
    const [activeIndex, setActiveIndex] = useState(1);

      const handleSlideChange = (index) => {
        setActiveIndex(index === activeIndex ? null : index);
      };






  return (
    <div>
                    <div className="relative lg:mx-3 xl:mx-8 hidden lg:block">
            <Swiper
        slidesPerView={3}
        spaceBetween={30}
        navigation={{
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
          }}
        slidesOffsetBefore = {15}
        slidesOffsetAfter = {15}
        modules={[Pagination]}
        breakpoints={{
            250: {
               slidesOffsetBefore : 50,
               slidesPerView:1.3,
               spaceBetween : 26
             },
             640: {
               slidesOffsetBefore : 100,
               slidesPerView:2.5,
               spaceBetween : 26
             },
             768: {
                slidesOffsetBefore : 15,
                slidesPerView:1,
                spaceBetween : 30,
                slidesOffsetAfter : 15,
             },
             1024: {
                slidesOffsetBefore : 15,
                slidesPerView:2,
                spaceBetween : 30,
                slidesOffsetAfter : 15,
             },
             1280: {
               slidesOffsetBefore : 15,
               slidesPerView:3,
               spaceBetween : 30,
               slidesOffsetAfter : 15,
             },
             
           }}
        className="mySwiper text-gray-500"
      >
        {data.map((card, index) => (
                    <SwiperSlide key={card.id}>
                        <div onClick={() => handleSlideChange(index)} className='w-fit relative' >
                        <img className={` ${activeIndex === index ? 'w-60 h-80' : 'w-36 h-52 mt-16'}`} src={card.source} alt="" />
                        <div className={` ${activeIndex === index ? 'absolute bottom-0 left-0 block' : 'hidden '}`}>
                            <p className='text-sm text-white px-5'>{card.decs}</p>
                            <div className='px-5 pb-8 pt-3 flex justify-between font-medium'>
                                <div className='flex gap-1 text-xs items-center'>
                                    <img src={bluePlay} alt="" />
                                    <p className='text-transparent bg-gradient-to-br from-teal-400 via-cyan-500 to-teal-500 bg-clip-text uppercase'>watch online</p>
                                </div>
                                <div className='flex gap-1 text-xs items-center'>
                                    <img src={donate} alt="" />
                                    <p className='text-white uppercase'>Donate</p>
                                </div>
                            </div>
                        </div>
                       
                        </div>
                    </SwiperSlide>))}

      </Swiper>
      <div className="swiper-button-next absolute top-1/2 -left-12  cursor-pointer">
      <svg width="15" height="27" viewBox="0 0 15 27" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M0.407272 14.1493L11.7695 25.8306C12.0323 26.101 12.3831 26.25 12.7571 26.25C13.1312 26.25 13.482 26.101 13.7448 25.8306L14.5815 24.9706C15.126 24.4102 15.126 23.4993 14.5815 22.9398L5.04041 13.1304L14.5921 3.31025C14.8549 3.03985 15 2.6794 15 2.29504C15 1.91026 14.8549 1.5498 14.5921 1.2792L13.7554 0.419355C13.4924 0.148962 13.1418 -1.62452e-07 12.7677 -1.95153e-07C12.3937 -2.27854e-07 12.0428 0.148962 11.7801 0.419355L0.407273 12.1114C0.143856 12.3826 -0.000824701 12.7448 4.9617e-06 13.1298C-0.000824769 13.5163 0.143856 13.8782 0.407272 14.1493Z" fill="url(#paint0_linear_1_2502)"/>
        <defs>
        <linearGradient id="paint0_linear_1_2502" x1="15" y1="13.125" x2="1.24521e-06" y2="13.125" gradientUnits="userSpaceOnUse">
        <stop stopColor="#EFA1AC"/>
        <stop offset="1" stopColor="#E783C5"/>
        </linearGradient>
        </defs>
        </svg>
      </div>
      <div className="swiper-button-prev absolute top-1/2 -right-24 cursor-pointer">
      <svg width="15" height="27" viewBox="0 0 15 27" fill="none" xmlns="http://www.w3.org/2000/svg">
        <path d="M14.5927 12.1007L3.23054 0.419356C2.96774 0.148962 2.61694 0 2.24288 0C1.86882 0 1.51802 0.148962 1.25522 0.419356L0.418477 1.27941C-0.126 1.83983 -0.126 2.75068 0.418477 3.31025L9.95959 13.1196L0.407891 22.9398C0.145097 23.2101 0 23.5706 0 23.955C0 24.3397 0.145097 24.7002 0.407891 24.9708L1.24464 25.8306C1.50764 26.101 1.85824 26.25 2.23229 26.25C2.60635 26.25 2.95716 26.101 3.21995 25.8306L14.5927 14.1386C14.8561 13.8674 15.0008 13.5052 15 13.1202C15.0008 12.7337 14.8561 12.3718 14.5927 12.1007Z" fill="url(#paint0_linear_1_2503)"/>
        <defs>
        <linearGradient id="paint0_linear_1_2503" x1="-9.77889e-08" y1="13.125" x2="15" y2="13.125" gradientUnits="userSpaceOnUse">
        <stop stopColor="#EFA1AC"/>
        <stop offset="1" stopColor="#E783C5"/>
        </linearGradient>
        </defs>
      </svg>

      </div>

            </div>
    </div>
  )
}

export default Swip
