import React, { useState, useEffect } from 'react';
import logoLight from '../img/logoLight.png'
import menu from '../img/menu.png'


function Navbar() {
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (darkMode) {
      document.documentElement.classList.add('dark');
    } else {
      document.documentElement.classList.remove('dark');
    }
  }, [darkMode]);

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className='mx-2.5 sm:mx-5 dark:bg-indigo-950  md:max-w-2xl lg:max-w-4xl  xl:max-w-6xl 2xl:max-w-screen-2xl md:mx-auto flex justify-between items-center py-6 '>
        <img src={logoLight} alt="" />
        <div className='hidden md:block'>
            <ol className='flex gap-12 xl:gap-20 text-gray-800 '>
                <li><a className='font-black underline' href="https://www.example.com/">About</a></li>
                <li><a href="https://www.example.com/">Zoos</a></li>
                <li><a href="https://www.example.com/">Map</a></li>
                <li><a href="https://www.example.com/">Contact</a></li>
                <li><a href="https://www.example.com/">Design</a></li>
            </ol>
        </div>
        <div className='flex gap-8'>
          <input
          class="mr-2 mt-[0.3rem] h-3.5 w-8 appearance-none rounded-[0.4375rem] bg-neutral-300 before:pointer-events-none before:absolute before:h-3.5 before:w-3.5 before:rounded-full before:bg-transparent before:content-[''] after:absolute after:z-[2] after:-mt-[0.1875rem] after:h-5 after:w-5 after:rounded-full after:border-none after:bg-neutral-100 after:shadow-[0_0px_3px_0_rgb(0_0_0_/_7%),_0_2px_2px_0_rgb(0_0_0_/_4%)] after:transition-[background-color_0.2s,transform_0.2s] after:content-[''] checked:bg-primary checked:after:absolute checked:after:z-[2] checked:after:-mt-[3px] checked:after:ml-[1.0625rem] checked:after:h-5 checked:after:w-5 checked:after:rounded-full checked:after:border-none checked:after:bg-primary checked:after:shadow-[0_3px_1px_-2px_rgba(0,0,0,0.2),_0_2px_2px_0_rgba(0,0,0,0.14),_0_1px_5px_0_rgba(0,0,0,0.12)] checked:after:transition-[background-color_0.2s,transform_0.2s] checked:after:content-[''] hover:cursor-pointer focus:outline-none focus:ring-0 focus:before:scale-100 focus:before:opacity-[0.12] focus:before:shadow-[3px_-1px_0px_13px_rgba(0,0,0,0.6)] focus:before:transition-[box-shadow_0.2s,transform_0.2s] focus:after:absolute focus:after:z-[1] focus:after:block focus:after:h-5 focus:after:w-5 focus:after:rounded-full focus:after:content-[''] checked:focus:border-primary checked:focus:bg-primary checked:focus:before:ml-[1.0625rem] checked:focus:before:scale-100 checked:focus:before:shadow-[3px_-1px_0px_13px_#3b71ca] checked:focus:before:transition-[box-shadow_0.2s,transform_0.2s] dark:bg-neutral-600 dark:after:bg-neutral-400 dark:checked:bg-primary dark:checked:after:bg-primary dark:focus:before:shadow-[3px_-1px_0px_13px_rgba(255,255,255,0.4)] dark:checked:focus:before:shadow-[3px_-1px_0px_13px_#3b71ca]"
          type="checkbox"
          role="switch"
          id="flexSwitchCheckDefault"
        />
        <img className='cursor-pointer' src={menu} alt="" />
        <div className="container mx-auto p-4">
      <div className="flex items-center justify-end mb-4">
        <label htmlFor="darkModeToggle" className="mr-2">Dark Mode</label>
        <input
          id="darkModeToggle"
          type="checkbox"
          checked={darkMode}
          onChange={toggleDarkMode}
          className="rounded-full h-6 w-10"
        />
      </div>
      <div className={`bg-white dark:bg-dark p-4 ${darkMode ? 'dark' : ''}`}>
        <h1 className="text-2xl font-bold mb-4">Welcome to my app</h1>
        <p className="text-gray-800 dark:text-white">
          This is an example of how to implement dark mode using Tailwind CSS in a React app.
        </p>
        {/* Rest of your app */}
      </div>
    </div>
        </div>
    </div>
  )
}

