import React, { useState } from 'react';
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import gorilla from '../img/gorilla.png';
import eagle from '../img/eagle.png';
import panda from '../img/panda.png';
import crocodile from '../img/crocodile.png';


import SwiperCore, { Pagination, Navigation } from 'swiper';

SwiperCore.use([Pagination, Navigation]);
const animals = [gorilla, eagle, panda, crocodile, gorilla, eagle, panda, crocodile]
function Pets() {
  return (
    <div>
        <div className='lg:max-w-[988px] xl:max-w-[1288px] mx-auto '>
            <h2 className='text-6xl text-gray-800 font-black mt-24 mb-8 dark:text-white text-center'>Pets in Zoo</h2>
            <div>
            <Swiper
        slidesPerView={4}
        spaceBetween={30}
        navigation={{
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
          }}

        modules={[Pagination]}
        breakpoints={{
            150: {
              slidesOffsetBefore: 0,
              slidesPerView: 2.2,
              spaceBetween: 9,
              },
              640: {
                slidesOffsetBefore: 0,
                slidesPerView: 2.4,
                spaceBetween: 30,
              },
              768: {
                slidesOffsetBefore: 0,
                slidesPerView: 3.5,
                spaceBetween: 30,
              },
              1024: {
                slidesPerView: 4,
                spaceBetween: 30,
              },
           }}
        className="mySwiper text-gray-500"
      >
        {animals.map((animal, index) => (
                    <SwiperSlide key={index}  >
                        <img src={animal} alt="" />
                    </SwiperSlide>))}

      </Swiper>
            </div>
        </div>
    </div>
  )
}

export default Pets
