import React, { useState, useRef, useEffect } from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import gorilla from '../img/gorilla.png';
import eagle from '../img/eagle.png';
import panda from '../img/panda.png';
import crocodile from '../img/crocodile.png';
import bluePlay from '../img/bluePlay.png';
import donate from '../img/donate.png';

import SwiperCore, { Pagination, Navigation } from 'swiper';
SwiperCore.use([Pagination, Navigation]);

const data = [
  {
    id: 1,
    source: eagle,
    decs: "Eagle's name is Aria. She is 5 years old. Aria symbolizes freedom, strength, and grace, soaring through the skies with majestic beauty",
  },
  {
    id: 2,
    source: panda,
    decs: 'Panda’s name is Bei Bei. He is 2 years old. Bei Bei is from China. He loves bamboos.',
  },
  {
    id: 3,
    source: gorilla,
    decs: "Gorilla's name is Koko. He is 10 years old. Koko is a powerful and intelligent creature from the lush forests. ",
  },
  {
    id: 4,
    source: crocodile,
    decs: "Crocodile's name is Rex. He is 15 years old. Rex is a formidable predator, lurking in the waters with stealth and power. ",
  },
  {
    id: 5,
    source: eagle,
    decs: "Eagle's name is Aria. She is 5 years old. Aria symbolizes freedom, strength, and grace, soaring through the skies with majestic beauty",
  },
  {
    id: 6,
    source: panda,
    decs: 'Panda’s name is Bei Bei. He is 2 years old. Bei Bei is from China. He loves bamboos.',
  },
  {
    id: 7,
    source: gorilla,
    decs: "Gorilla's name is Koko. He is 10 years old. Koko is a powerful and intelligent creature from the lush forests. ",
  },
  {
    id: 8,
    source: crocodile,
    decs: "Crocodile's name is Rex. He is 15 years old. Rex is a formidable predator, lurking in the waters with stealth and power. ",
  },
];

function Swip() {
  const [activeIndex, setActiveIndex] = useState(1);
  const swiperRef = useRef(null);

  const handleSlideChange = (index) => {
    setActiveIndex(index === activeIndex ? null : index);
    if (swiperRef.current && swiperRef.current.swiper) {
      swiperRef.current.swiper.update();
    }
  };

  useEffect(() => {
    const importSwiperCSS = async () => {
      const { default: SwiperCSS } = await import('swiper/css');
      SwiperCSS.use();
    };

    importSwiperCSS();
  }, []);

  useEffect(() => {
    const importPaginationCSS = async () => {
      const { default: PaginationCSS } = await import('swiper/css/pagination');
      PaginationCSS.use();
    };

    importPaginationCSS();
  }, []);

  return (
    <div>
      <div className="relative lg:mx-3 xl:mx-8 md:w-2/3">
        <Swiper
          ref={swiperRef}
          slidesPerView={3}
          spaceBetween={30}
          navigation={{
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
          }}
          modules={[Pagination]}
          breakpoints={{
            150: {
              slidesOffsetBefore: 10,
              slidesPerView: 2.2,
              spaceBetween: 9,
            },
            640: {
              slidesOffsetBefore: 50,
              slidesPerView: 2.2,
              spaceBetween: 30,
            },
            1200: {
              slidesOffsetBefore: 100,
              slidesPerView: 2.5,
              spaceBetween: 30,
            },
            1280: {
              slidesOffsetBefore: 100,
              slidesPerView: 4.5,
              spaceBetween: 46,
            },
          }}
          className="mySwiper text-gray-500"
        >
          {data.map((card, index) => (
            <SwiperSlide key={card.id} style={{ width: 'auto' }}>
              <div className="flex justify-center">
                <div
                  onClick={() => handleSlideChange(index)}
                  className={`relative ${
                    activeIndex === index
                      ? ' scale-x-[1.45] h-[202px] sm:h-[352px]'
                      : 'sm:mt-16 w-[113px] h-[164px] sm:w-[140px] sm:h-[203px]'
                  }`}
                >
                  <img className="w-full h-full" src={card.source} alt="" />
                  <div
                    className={`${
                      activeIndex === index ? 'absolute bottom-0 left-0 block' : 'hidden '
                    }`}
                  >
                    <p className="text-sm text-white px-5">{card.decs}</p>
                    <div className="px-5 pb-8 pt-3 flex gap-2.5 font-medium text-[10px]">
                      <div className="flex gap-1 items-center">
                        <img src={bluePlay} alt="" />
                        <p className="text-transparent bg-gradient-to-br from-teal-400 via-cyan-500 to-teal-500 bg-clip-text uppercase">
                          watch online
                        </p>
                      </div>
                      <div className="flex gap-1 items-center">
                        <img src={donate} alt="" />
                        <p className="text-white uppercase">Donate</p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
}

export default Swip;
